void Game::Ball_collision_check() {

    //collision with the level borders
    if (x_ball < 0) {
        x_ball = 50;
        y_ball = 50;
        //xvel_ball = xvel_ball*-1;
        alive_ball = FALSE;
    }
    if (x_ball > 800 - width_ball) {
        x_ball = 50;
        y_ball = 50;
        //xvel_ball = xvel_ball*-1;
        alive_ball = FALSE;
    }
    if (y_ball < 0) {
        y_ball = 0;
        yvel_ball = yvel_ball*-1;
        wallHit.Play(rng);
    }
    if (y_ball > 600 - height_ball) {
        y_ball = 600 - height_ball;
        yvel_ball = yvel_ball*-1;
        wallHit.Play(rng);
    }
    //collision with the level borders for ball 2
    if (x_ball2 < 0) {
        x_ball2 = 50;
        y_ball2 = 50;
        //xvel_ball = xvel_ball*-1;
        alive_ball2 = FALSE;
    }
    if (x_ball2 > 800 - width_ball) {
        x_ball2 = 50;
        y_ball2 = 50;
        //xvel_ball = xvel_ball*-1;
        alive_ball2 = FALSE;
    }
    if (y_ball2 < 0) {
        y_ball2 = 0;
        yvel_ball2 = yvel_ball2*-1;
        wallHit.Play(rng);
    }
    if (y_ball2 > 600 - height_ball) {
        y_ball2 = 600 - height_ball;
        yvel_ball2 = yvel_ball2*-1;
        wallHit.Play(rng);
    }

    //Collision and bounce with player1 for ball1
    if (x_ball < x_player1 + width_player && 
        y_ball + height_ball/2 <y_player1 + height_player && 
        y_ball + height_ball/2>y_player1) 
    {
        //x_ball = x_player1 + width_player;
        paddleHit.Play(rng);
        xvel_ball = abs(xvel_ball);

        //y-vel calculation ie. the angle at which the balle leaves the paddle. Paddle is split into 4 equal sections
        if (y_ball + height_ball / 2 < y_player1 + height_player/4) {
            yvel_ball = -3;
        }
        else if (y_ball + height_ball / 2 < y_player1 + (height_player / 4)*2) {
            yvel_ball = -1.0;
        }
        else if (y_ball + height_ball / 2 < y_player1 + (height_player / 4) * 3) {
            yvel_ball = 1.0;
        }
        else {
            yvel_ball = 3;
        }
    }

    //Collision and bounce with player1 for ball2
    if (x_ball2 < x_player1 + width_player &&
        y_ball2 + height_ball / 2 <y_player1 + height_player &&
        y_ball2 + height_ball / 2>y_player1)
    {
        //x_ball = x_player1 + width_player;
        xvel_ball2 = abs(xvel_ball2);
        paddleHit.Play(rng);

        //y-vel calculation ie. the angle at which the balle leaves the paddle. Paddle is split into 4 equal sections
        if (y_ball2 + height_ball / 2 < y_player1 + height_player / 4) {
            yvel_ball2 = -3;
        }
        else if (y_ball2 + height_ball / 2 < y_player1 + (height_player / 4) * 2) {
            yvel_ball2 = -1.0;
        }
        else if (y_ball2 + height_ball / 2 < y_player1 + (height_player / 4) * 3) {
            yvel_ball2 = 1.0;
        }
        else {
            yvel_ball2 = 3;
        }
    }

    

    //Collision and bounce with player2 for ball 1
    if (x_ball+32 > x_player2 &&
        y_ball + height_ball / 2 <y_player2 + height_player &&
        y_ball + height_ball / 2>y_player2)
    {
        //x_ball = x_player2 + width_player;
        xvel_ball = abs(xvel_ball)*-1;
        paddleHit.Play(rng);

        //y-vel calculation ie. the angle at which the balle leaves the paddle. Paddle is split into 4 equal sections
        if (y_ball + height_ball / 2 < y_player2 + height_player / 4) {
            yvel_ball = -3 * (120 / fps);
        }
        else if (y_ball + height_ball / 2 < y_player2 + (height_player / 4) * 2) {
            yvel_ball = -1.0* (120 / fps);
        }
        else if (y_ball + height_ball / 2 < y_player2 + (height_player / 4) * 3) {
            yvel_ball = 1.0* (120 / fps);
        }
        else {
            yvel_ball = 3 * (120 / fps);
        }
    }

    //Collision and bounce with player2 for ball 2
    if (x_ball2 + 32 > x_player2 &&
        y_ball2 + height_ball / 2 <y_player2 + height_player &&
        y_ball2 + height_ball / 2>y_player2)
    {
        //x_ball = x_player2 + width_player;
        xvel_ball2 = abs(xvel_ball2)*-1;
        paddleHit.Play(rng);
        //y-vel calculation ie. the angle at which the balle leaves the paddle. Paddle is split into 4 equal sections
        if (y_ball2 + height_ball / 2 < y_player2 + height_player / 4) {
            yvel_ball2 = -3 * (120 / fps);
        }
        else if (y_ball2 + height_ball / 2 < y_player2 + (height_player / 4) * 2) {
            yvel_ball2 = -1.0* (120 / fps);
        }
        else if (y_ball2 + height_ball / 2 < y_player2 + (height_player / 4) * 3) {
            yvel_ball2 = 1.0* (120 / fps);
        }
        else {
            yvel_ball2 = 3 * (120 / fps);
        }
    }
}
