void Game::UpdateModel()
{
    if (!mainMusicPlaying) {
        mainTheme.Play();
        mainMusicPlaying = true;
    }
    if (level1_clear && doOnce1)
    {
        gameStart.Play();
        doOnce1 = false;
    }
    //reset game
    if (wnd.kbd.KeyIsPressed(VK_SPACE)) {

        if (win_screen) {
            level2_screen = false;
            level1_clear = false;
            level2_clear = false;
        }
        if (level1_clear) {
            level2_built = false;
        }

        x_player1 = 10;
        y_player1 = 200;
        x_player2 = 758;
        y_player2 = 200;
        width_player = 24;
        height_player = 160;

        y_ball2 = 200;
        x_ball2 = 600;
        xvel_ball2 = 3 * (120 / fps);
        yvel_ball2 = 3 * (120 / fps);

        y_ball = 200;
        x_ball = 50;
        xvel_ball = -3 * (120 / fps);
        yvel_ball = 3 * (120 / fps);
        width_ball = 24;
        height_ball = 24;
        alive_ball = true;
        alive_ball2 = true;
        level1_built = false;
        even = true;
        game_start = true;
        win_screen = false;
        running = true;
        useonce = true;
        gameOverOnce = true;

    }


    if (!level1_built && !level1_clear) {
        BuildLevel1();
        level1_built = true;
    }
    if (!level2_built && level1_clear) {
        BuildLevel2();
        level2_built = true;
    }



    //movement of players
    if (wnd.kbd.KeyIsPressed('W')) {
        y_player1 -= 4 * (120 / fps);
    }
    if (wnd.kbd.KeyIsPressed('S')) {
        y_player1 += 4 * (120 / fps);
    }
    if (wnd.kbd.KeyIsPressed(VK_UP)) {
        y_player2 -= 4 * (120 / fps);
    }
    if (wnd.kbd.KeyIsPressed(VK_DOWN)) {
        y_player2 += 4 * (120 / fps);
    }



    Player_collision_check();
    Brick_collision_check();
    if (running)
    {

        if (alive_ball) {
            x_ball += xvel_ball;
            y_ball += yvel_ball;
        }
        if (alive_ball2) {
            x_ball2 += xvel_ball2;
            y_ball2 += yvel_ball2;
        }

    }
    Ball_collision_check();

    //game over check
    if (!alive_ball && !alive_ball2) {
        game_start = false;

        if(gameOverOnce)
        {
            gameOver.Play();
            gameOverOnce = false;
        }
        
    }

    //check if level is clear

    if (!level2_clear && level1_clear)
    {
        level2_clear = true;
        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 9; j++) {
                if (bricks[i][j].IsAlive()) {
                    level2_clear = false;
                    goto endloop2;
                }
            }
        }
    }
endloop2:


    if (!level1_clear)
    {
        level1_clear = true;
        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 9; j++) {
                if (bricks[i][j].IsAlive()) {
                    level1_clear = false;
                    goto endloop;
                }
            }
        }
    }
endloop:



    if (level1_clear && level2_clear) {
        win_screen = true;
        running = false;

        if (doOnce2)
        {
            mainTheme.StopAll();
            gameStart.Play();
            doOnce2 = false;
        }
        
    }


    //Cheat, destroy all blocks if k is pressed
    if (wnd.kbd.KeyIsPressed('K') && useonce)
    {
        useonce = false;
        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 9; j++) {
                bricks[i][j].Destroy();
                    
                
            }
        }
    }
}

void Game::ComposeFrame()
{
    if (!game_start) {

        DrawStartScreen(400 - (384 / 2), 300 - 50);

        
    }
    else if (win_screen) {
        DrawWinScreen(400 - (274 / 2), 300 - 24);
    }
    else if (level2_screen) {

    }
    else {

        DrawPlayer1(x_player1, y_player1);
        DrawPlayer2(x_player2, y_player2);

        if (alive_ball) {
            DrawBall(x_ball, y_ball);
        }
        if (alive_ball2) {
            DrawBall2(x_ball2, y_ball2);
        }

        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 9; j++) {
                if (bricks[i][j].IsAlive()) {
                    if (bricks[i][j].GetType() == 'g')
                    {
                        DrawBrickGrey((bricks[i][j]).GetX(), (bricks[i][j]).GetY());
                    }
                    else if (bricks[i][j].GetType() == 'r')
                    {
                        DrawBrickRed((bricks[i][j]).GetX(), (bricks[i][j]).GetY());
                    }
                    else if (bricks[i][j].GetType() == 'b')
                    {
                        DrawBrickBlue((bricks[i][j]).GetX(), (bricks[i][j]).GetY());
                    }
                }
            }
        }
    }

}
